public class LevelCounter {

  public static int countingValleys(int steps, String path) {
    // Write your code here
    int level = 0;
    int totalValleys = 0;
    boolean newValley = true;

    for (int i = 0; i < steps; i++) {
      if (path.charAt(i) == 'U'){
        level++;
      } else if (path.charAt(i) == 'D') {
        level--;
      }
      if(level == 0){
        newValley = true;
      }
      if(level == -1 && newValley){
        totalValleys++;
        newValley = false;
      }
    }
    return totalValleys;
  }


  public static void main(String[] args) {
    String path = "";
    System.out.println(countingValleys(1000, path));
  }

}
